import React from "react";
import { connect } from "react-redux";
import { Row, Col, Carousel } from 'react-materialize';
import { parseColorSelected } from "../actions";



const Color = ({state, parseColorSelected}) => {



    let mappedColor = state.jsonVersion.characteristic

    const getColor = (currentSelect) => {

            parseColorSelected(currentSelect)

    }


    const displayColor = () => mappedColor.map((color, key) => {

        return (
            <div key={color}>
                <div className="container">

                    <Col className="teal white-text box" s={6} m={4} >
                       <div className="custom-color-select">

                       <img src={color.rims[0].pictures[0]} onClick={() => (getColor(color))}/>
                       {color.name}
                       </div>

                    </Col>
                </div>

            </div>
        )


    })

    const displayCarrousel = () => state.currentSelection.view.map((pictures) => {

        return (
            <Carousel
                carouselId="Carousel-31"
                images={[
                    {pictures}
                ]}
                options={{
                    dist: -100,
                    duration: 200,
                    fullWidth: false,
                    indicators: false,
                    noWrap: false,
                    numVisible: 5,
                    onCycleTo: null,
                    padding: 0,
                    shift: 0
                }}
            /> 

        )


    })

    console.log(state);





    return(

       <div>
           {state.currentSelection.name}
           <div className="container">
               {displayCarrousel()}

           </div>

            <Row>
            {displayColor()}

            </Row>
         colorrrrrr
        </div>
    )}

const mapStateToProps = state =>{
    return{
        state: state,
    }
}
const mapDispatchToProps = dispatch => {
    return{

        parseColorSelected: (data)=> dispatch(parseColorSelected(data)),

    }
}
export default connect(mapStateToProps, mapDispatchToProps)(Color)
