import React from "react";
import { connect } from "react-redux";
import { Carousel, Row, Col, Card, CardTitle, Collection, CollectionItem } from 'react-materialize'; 


const Multimedia = ({state, multimedia}) => {

    console.log("state multimedia : " , state);

    const onChange = (data) => {


        console.log("DATA GET ONCHANGE: ", data);
        console.log("SELECTION COURANTE: ", state.currentSelection);

        if(state.currentSelection.accessories.multimedia.support === null){
            getSupport(data)

        }else{
            deleteSupport()
        }


        // if(selection === 'Exhaust'){
        //     if(state.currentSelection.exhaust === null){
        //         getExhaust(data)

        //     }else{
        //         deleteExhaust()
        //     }
        // }
        // if(selection !== 'Exhaust'){

        //     if(state.currentSelection.parkassist === null){
        //             getParkAssist(data)
        //     }else if(state.parkassist !== null){
        //         if(state.currentSelection.parkassist.name === selection){
        //             deleteParkAssist()

        //         }else{
        //             getParkAssist(data)
        //         }
        //     }
        // }
      }

    const displayMultimedia = () => multimedia.map((option) => {

        console.log("option : ", option);

        return (
            <Col m={3} s={12} >
                <Card className='itemDriving'
                key={option}
                header={<CardTitle image={option.picture}
                onClick={() => onChange(option)}/>}
                > 
                    <p className='equipementName'>{option.name}</p>
                    <p>{option.price} <i class="material-icons">attach_money</i></p>
                </Card> 
            </Col>
        )
        })

    return(
        <div className='driving'>
            <Row>
                <Col m={8} s={12}>
                    <Carousel
                    carouselId="Carousel-61"
                    images={[
                        multimedia[0].picture,
                    ]}
                    options={{
                        fullWidth: true,
                        indicators: true
                    }}
                    />
                </Col>
                <Col m={4} s={12}>
                    <Row>
                        <Col
                            m={6}
                            s={12}
                        >
                            <Collection>
                            <CollectionItem href="#">
                                Alvin
                            </CollectionItem>
                            <CollectionItem
                                active
                                href="#"
                            >
                                Alvin
                            </CollectionItem>
                            <CollectionItem href="#">
                                Alvin
                            </CollectionItem>
                            <CollectionItem href="#">
                                Alvin
                            </CollectionItem>
                            </Collection>
                        </Col>
                    </Row>
                </Col>
            </Row>
            <Row>
                <Col m={3} s={12} >
                </Col>
                {
                    displayMultimedia()
                }
            </Row>
        </div>
    )}

const mapStateToProps = state =>{
    return{
        state : state,
        multimedia : state.jsonOption.accessories.multimedia.support
    }
}
const mapDispatchToProps = dispatch => {
    return{

    }
}
export default connect(mapStateToProps, mapDispatchToProps)(Multimedia)
