import React, {useEffect} from "react";
import { connect } from "react-redux";
import { Link } from 'react-router-dom';
import { Row, Col, Button, Carousel } from 'react-materialize';
import Menu from "./Menu";

const Configurator = ({state}) => {

return(
    <div>
        <Menu />
        <Row className='configurator'>
            <h3 className="presentation-version">{state.version}</h3> 
            {state.version === "Pure" &&
                    <Carousel
                        carouselId="Carousel-35"
                        className='carouselHome'
                        images={[
                            "/AlpineCars_app-images/sources-homepage/galerie/A110_PURE_4.jpg",
                            "/AlpineCars_app-images/sources-homepage/galerie/A110_PURE_6.jpg",
                            "/AlpineCars_app-images/sources-homepage/galerie/A110_PURE_8.jpg"
                        ]}
                        options={{
                            fullWidth: true,
                            indicators: true,
                            centerImages: true,
                        }}
                    />  
            }
            {state.version === "Legende" &&
                <Col s={12} m={12} className="configurator-img">
                    <img src="/AlpineCars_app-images/sources-homepage/A110/Alpine-A110-1.jpg"></img>
                </Col>
            }
        </Row>

    </div>
)}
const mapStateToProps = state =>{
    return{
        state: state,
    }
}
const mapDispatchToProps = dispatch => {
    return{
    }
}
export default connect(mapStateToProps, mapDispatchToProps)(Configurator)
