import React from "react";
import { connect } from "react-redux";
import { Link } from 'react-router-dom';
import { getEquipementPannel } from "../../actions";


const Accessories = () => {

return(

    <div className="router-equipement">
        <div className="router-item" id={state.equipementPannel === "conduite" ? "pannelSelected" : ""}><Link to= "/Exterieur"><i class="fas fa-car"></i></Link></div>
        <div className="router-item" id={state.equipementPannel === "conduite" ? "pannelSelected" : ""}><Link to= "/Garage"><i class="fas fa-warehouse"></i></Link></div>
        <div className="router-item" id={state.equipementPannel === "conduite" ? "pannelSelected" : ""}><Link to= "/Interieur"><i class="fas fa-atlas"></i></Link></div>
        <div className="router-item" id={state.equipementPannel === "conduite" ? "pannelSelected" : ""}><Link to= "/Multimedia"><i class="fas fa-photo-video"></i></Link></div>
        <div className="router-item" id={state.equipementPannel === "conduite" ? "pannelSelected" : ""}><Link to= "/Transport"><i class="fas fa-archive"></i></Link></div>
    </div>
)}
const mapStateToProps = state =>{
    return{
        state : state,
    }
}
const mapDispatchToProps = dispatch => {
    return{
        getEquipementPannel: (data)=> dispatch(getEquipementPannel(data)),
    }
}
export default connect(mapStateToProps, mapDispatchToProps)(Accessories)
