import React from "react";
import { connect } from "react-redux";
import { Row, Col } from 'react-materialize';
import { parseColorSelected } from "../actions";



const Color = ({state}) => {

    console.log("state color :" , state.jsonVersion.characteristic);


    let mappedColor = state.jsonVersion.characteristic
    console.log(mappedColor);

    const getColor = (currentSelect) => {

        console.log("info d'entré : ", currentSelect);
        if(currentSelect.color === "blue"){

            console.log("blue ? :" ,currentSelect.color);
            parseColorSelected(currentSelect)

            console.log("state good? :" , state);


        }else if(currentSelect.color === "black"){

            console.log("black ? :" ,currentSelect.color);
            parseColorSelected(currentSelect)


            console.log("state good? :" , state);


        }else if(currentSelect.color === "white"){
            console.log("white ? :" ,currentSelect.color);
            parseColorSelected(currentSelect)


            console.log("state good? :" , state);

        }
    }



    const displayColor = () => mappedColor.map((color, key) => {

        return (
            <div key={color}>
                <div className="container">

                    <Col className="teal white-text box" s={6} m={4} >
                       <div className="custom-color-select">

                       <img src={color.rims[0].pictures[0]} onClick={() => (getColor(color))}/>
                       {color.name}
                       </div>

                    </Col>
                </div>

            </div>
        )


    })



    return(

       <div>
           <div className="container">

           </div>

            <Row>
            {displayColor()}

            </Row>
         colorrrrrr
        </div>
    )}

const mapStateToProps = state =>{
    return{
        state: state,
    }
}
const mapDispatchToProps = dispatch => {
    return{

        parseColorSelected:(currentSelection)=> dispatch(parseColorSelected(currentSelection)),

    }
}
export default connect(mapStateToProps, mapDispatchToProps)(Color)
