import React from "react";
import { connect } from "react-redux";
import { Carousel, Row, Col, Card, CardTitle, Collection, CollectionItem } from 'react-materialize';
import { getInnerAccessories , deleteInnerAccessories } from "../../actions"; 


const Interieur = ({state, innerAccessories, getInnerAccessories, deleteInnerAccessories}) => {


    console.log("State : ", state);

    const onTransport = (data) => {

        if(state.transport.length === 0){
            getTransport(data)
        }else{
                for(var i = 0; i < state.transport.length; i++){
                      if(state.transport[i].name === data.name){
                        deleteTransport(data);
                        break;
                    }else if(state.transport[i].name !== data.name){
                        getTransport(data);
                        break;
                    }
                }

        }
}


    const displayTransport = () => transport.map((option) => {

        return (
            <Col m={3} s={12} >
                <Card className='itemDriving'
                key={option}
                header={<CardTitle image={option.picture}
                onClick={() => onTransport(option)}/>}
                > 
                    <p className='equipementName'>{option.name}</p>
                    <p>{option.price} <i class="material-icons">attach_money</i></p>
                </Card> 
            </Col>
        )
        })

    return(
        <div className='driving'>
            <Row>
                <Col m={8} s={12}>
                    <Carousel
                    carouselId="Carousel-61"
                    images={[
                        transport[0].picture,
                    ]}
                    options={{
                        fullWidth: true,
                        indicators: true
                    }}
                    />
                </Col>
                <Col m={4} s={12}>
                    <Row>
                        <Col
                            m={6}
                            s={12}
                        >
                            <Collection>
                            <CollectionItem href="#">
                                Alvin
                            </CollectionItem>
                            <CollectionItem
                                active
                                href="#"
                            >
                                Alvin
                            </CollectionItem>
                            <CollectionItem href="#">
                                Alvin
                            </CollectionItem>
                            <CollectionItem href="#">
                                Alvin
                            </CollectionItem>
                            </Collection>
                        </Col>
                    </Row>
                </Col>
            </Row>
            <Row>
                <Col m={3} s={12} >
                </Col>
                {
                    displayTransport()
                }
            </Row>
        </div>
    )}

const mapStateToProps = state =>{
    return{
        state : state,
        innerAccessories : state.jsonOption.accessories.interior,
    }
}
const mapDispatchToProps = dispatch => {
    return{
        getInnerAccessories: (data) =>  dispatch(getInnerAccessories(data)),
        deleteInnerAccessories: (data) =>  dispatch(deleteInnerAccessories(data))
    }
}
export default connect(mapStateToProps, mapDispatchToProps)(Interieur)
